<?class Pages {public static $page;public static $error_text;var $file_ex;var $page_from_base;function __construct(){	$this->get_page();}function get_page(){	if($_GET[page] == "") { $_GET[page] = "index"; }		$query = mysql_query("SELECT * FROM pages WHERE url='$_GET[page]'");	if(mysql_num_rows($query) > 0)	{		$array = mysql_fetch_array($query);		Pages::$page = "pages/page.php";		$this->page_from_base = $array;	}	else	{		$file = "pages/".$_GET[page].".php";		$this->check_page($file);		if($this->file_ex == TRUE)		{			Pages::$page = "pages/".$_GET[page].".php";		}		else		{			Pages::$page = "pages/error.php";			Pages::get_error("404");		}	}}function check_page($file){	if(file_exists($file))	{		$this->file_ex = TRUE;	}	else	{		$this->file_ex = FALSE;	}}static function get_error($error){	if($error == "")	{		$error = "404";	}		switch($error)	{		case "404":		Pages::$page = "pages/error.php";		Pages::$error_text = "<div style='margin:0 auto;'><h1>Ошибка 404. Страница не найдена</h1></div>		<div style='margin:0 auto;'>Этой страницы или вообще не существовало, или же она была недавно удалена. Попробуйте поискать что-то другое</div>";		break;				case "502":		Pages::$page = "pages/error.php";		Pages::$error_text = "<div style='margin:0 auto;'><h1>Ошибка 502. Сервер временно занят</h1></div>		<div style='margin:0 auto;'>Сервер не отвечает. Подождите немного и попробуйте снова</div>";		break; 				case "403":		Pages::$page = "pages/error.php";		Pages::$error_text = "<div style='margin:0 auto;'><h1>Ошибка 403. Доступ к странице запрещен</h1></div>		<div style='margin:0 auto;'>Простите, но Вам запрещено просматривать даную страницу</div>";		break; 	}}}