ption = "Личный кабинет зарегистрированого пользователя";		$this->keywords = "кабинет, управление, пользователь, аккаунт";	}		elseif($_GET[page] != "") //Остальные страницы	{		//$eng_title = str_replace("_", " ", $_GET[page]);		$eng_title = $_GET[page];		$array = mysql_fetch_array(mysql_query("SELECT title, description, keywords FROM pages WHERE url='$eng_title' AND vision='1'"));		if($array[title] != "")		{			$this->title = $array[title];			$this->description = $array[description];			$this->keywords = $array[keywords];		}	}}}