<?class Translate {Var $text = "";Var $switcher = "RU <a href='/ua/'>UA</a> <a href='/en/'>EN</a>";function take_cookie($lang){	if($_COOKIE['lang'] == "")	{		setcookie("lang", $lang);	}	else	{		$_COOKIE['lang'] = $lang;	}}function get_text($old_text){	$array = mysql_fetch_array(mysql_query("SELECT ".$_COOKIE['lang']." FROM text WHERE RU='".$old_text."'"));	return $array[$_COOKIE['lang']];}function get_switcher(){	if($_COOKIE['lang'] != "RU" AND $_COOKIE['lang'] != "") { $switcher = "<a href='/ru/'>RU</a>"; } else { $this->switcher = "RU"; }	if($_COOKIE['lang'] != "UA") { $this->switcher .= " <a href='/ua/'>UA</a>";} else { $switcher .= "UA"; }	if($_COOKIE['lang'] != "EN") { $this->switcher .= " <a href='/en/'>EN</a>";} else { $switcher .= "EN"; }}}