<?
class Pages {var $page;var $file_exist;function __construct(){	$this->get_page();}function get_page(){	$this->check_page("/pages/$_GET[page]")	if($this->file_exist == TRUE)	{		if($_GET[page] == "")		{			$this->page = "/pages/index.php";		}		else		{			$this->page = "/pages/".$_GET[page];		}	}	else	{		$this->get_error("404");	}}	function check_page($file){	if($_GET[page] != "" AND file_exists($file))	{		$this->file_exist = TRUE;	}	else	{		$this->file_exist = FALSE;	}}function get_error($error){	else	{		$this->page = "/pages/error.php";		$this->page_error = "		<div style='text-align:center;'><p><h1>Ошибка 404</h1></p>		<p>Запрошеная Вами страница не найдена</p>		</div>";	}}}